import styled from "styled-components";
import Button from "../Button/Button";

export interface ConfirmProps {
  title: string
  onConfirm: () => any
  onCancel: () => any
}

export default function Confirm (props: ConfirmProps) {
  return <ConfirmWrapper>
    <ConfirmTitle>{props.title}</ConfirmTitle>
    <ConfirmButtonDisplay>
      <Button variant="danger" label="NÃ£o" onClick={props.onCancel}/>
      <Button variant="primary" label="Sim" onClick={props.onConfirm}/>
    </ConfirmButtonDisplay>
  </ConfirmWrapper>
}

const ConfirmWrapper = styled.div`
  padding: 24px;
  display: flex;
  flex-direction: column;
  gap: 12px;
  align-items: center;
  justify-content: center;
  background-color: #F3F8FA;
  color: #274060;
  width: 229px;
`

export const ConfirmTitle = styled.h2`
  font-size: 18px;
  font-weight: 700;
  display: -webkit-box;
  -webkit-line-clamp: 1;
  -webkit-box-orient: vertical;
  line-clamp: 1;
  overflow: hidden;
`

export const ConfirmButtonDisplay = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 8px;
`
